    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        try{
            Connection con = DriverManager.getConnection("jdbc:mysql://localhost:3306/customer_vehicle","root","javadbms");
            Statement s = con.createStatement();
            ResultSet rs = s.executeQuery("SELECT customer.name,customer.licence_no, customer.ph_no,customer.starting_point,customer.destination,vehicle.model_name,route1.distance FROM ((customer INNER JOIN route1 ON customer.route_id = route1.id) INNER JOIN vehicle ON customer.vehicle_id = vehicle.id) where customer.id= (select max(id) from customer)");
            while(rs.next()) {
                String name = rs.getString("name");
                String lno = rs.getString("licence_no");
                String phn = rs.getString("ph_no");
                String src = rs.getString("starting_point");
                String des = rs.getString("destination");
                String mod = rs.getString("model_name");
                String dist = String.valueOf(rs.getDouble("distance"));


                double amt =1;
                if (src.equals("Somanur") && des.equals("Gandhipuram")|| (des.equals("Somanur") && src.equals("Gandhipuram")) ) {
                    amt= 8.4*27;
                }

                if(src.equals("Peelamedu") && des.equals("Somanur") || (src.equals("Singanallur") && des.equals("Peelamedu"))){
                    amt= 4.8*27;
                }

                if(src.equals("Gandhipuram") && des.equals("Peelamedu") || (src.equals("Peelamedu") && des.equals("Gandhipuram"))){
                   amt= 6.8*27;
                }

                String jtd[] = {name,lno,phn,src,des,mod,dist};
                DefaultTableModel tab = (DefaultTableModel)jTable1.getModel();
                tab.addRow(jtd);
                JOptionPane.showMessageDialog(this,"You have successfully rented the vehicle!\nPlease go to "+src+" and pay the amount of â‚¹"+amt+" and collect your vehicle");
            }
            con.close();
        }
        catch(Exception e)
        {
            System.out.println(e);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

